// TagInput.tsx
import React, { useState, KeyboardEvent, FocusEvent, ChangeEvent } from 'react';

type TagInputProps = {
  value: string[];
  onChange: (tags: string[]) => void;
  separator?: string;
  maxTags?: number;
  placeholder?: string;
  className?: string;
};

const TagInput: React.FC<TagInputProps> = ({
  value,
  onChange,
  separator = ',',
  maxTags,
  placeholder = 'Enter tags',
  className = '',
}) => {
  const [input, setInput] = useState('');

  const addTags = (rawValue: string) => {
    const tags = rawValue
      .split(separator)
      .map((tag) => tag.trim())
      .filter((tag) => tag.length > 0 && !value.includes(tag));

    const newTags = maxTags ? [...value, ...tags].slice(0, maxTags) : [...value, ...tags];

    if (newTags.length !== value.length) {
      onChange(newTags);
    }
    setInput('');
  };

  const removeTag = (index: number) => {
    onChange(value.filter((_, i) => i !== index));
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' || e.key === separator) {
      e.preventDefault();
      addTags(input);
    }
  };

  const handleBlur = (e: FocusEvent<HTMLInputElement>) => {
    if (input.trim()) {
      addTags(input);
    }
  };

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
  };

  return (
    <div className={`border p-2 rounded flex flex-wrap gap-1 ${className}`}> 
      {value.map((tag, index) => (
        <div key={index} className="bg-gray-200 px-2 py-1 rounded flex items-center">
          <span className="mr-1">{tag}</span>
          <button
            type="button"
            className="text-red-500 ml-1"
            onClick={() => removeTag(index)}
          >
            &times;
          </button>
        </div>
      ))}
      {(!maxTags || value.length < maxTags) && (
        <input
          type="text"
          className="flex-1 min-w-[100px] focus:outline-none"
          value={input}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          onBlur={handleBlur}
          placeholder={placeholder}
        />
      )}
    </div>
  );
};

export default TagInput;
